#include "stdio.h"

// check for repetition in 4char buffer
int checkRepetition(char* buf) {
    for(int i=0; i<4; ++i) {
        for(int j=0; j<4; ++j) {
            if (i==j) {
                continue;
            } else if (buf[i] == buf[j]) {
                return 1;
            }
        }
    }
    return 0;
}

// shift 4char buffer one place to left
void shift(char *buf) {
    for (int i=0; i<4; ++i) {
        buf[i] = buf[i+1];
    }
}

int main(int argc, char** argv) {
    char code[4096] = "";
    char buffer[5] = {code[0], code[1], code[2], code[3]};
    int marker;
    for(int i=4; i<4096; i++) {
        if(!checkRepetition(buffer)) {
            marker = i;
            break;
        } else {
            shift(buffer);
            buffer[3] = code[i];
        }
    }
    
    printf("%d", marker);
    
    return 0;
}